choose.when:
select * from permission where is_delete = 0

	and type = #{type, typeHandler = com.lin.shiro.type.handler.EnumTypeHandler} 

limit #{offset}, #{length}

choose.otherwise:
select per.* from user_permission uPer left join permission per on per.id = uPer.permission_id where per.is_delete = 0 and uPer.user_id = #{userId} 

	and per.type = #{type, typeHandler = com.lin.shiro.type.handler.EnumTypeHandler} 

limit #{offset}, #{length}
